import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Alert from '@mui/material/Alert';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';

import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import Input from '@mui/material/Input';
import FilledInput from '@mui/material/FilledInput';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import TextField from '@mui/material/TextField';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import { green } from '@mui/material/colors';
import Icon from '@mui/material/Icon';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import DeleteIcon from '@mui/icons-material/Delete';

let Main =()=>{
 



const [text, setText] = React.useState("");

const handleClickShowPassword = () => setShowPassword((show) => !show);

const handleMouseDownPassword = (event) => {
  event.preventDefault();
};




    /*Get Year */
    const year = new Date().getFullYear();
    
    
    
    return(
        <>
            

    <Container>
      <Row>
        <Col>
        
        <h1 className='text-center'>Lets Play!!</h1>

   


        <Card style={{ width: '23rem'}}>
        
      <Card.Body style={{"height":"150px","borderRadius":"15px"}}>
        
     
        <Card.Title>
            
      <FormControl fullWidth sx={{ m: 1 }} variant="standard">
          <InputLabel htmlFor="standard-adornment-amount">Add Tasks</InputLabel>
          <Input
            id="standard-adornment-amount"
            type="text"
            value={value}
            onChange={(e)=>setText(e.target.value)}
            endAdornment={
                <InputAdornment position="end">
                    <AddCircleIcon 
                    aria-label="toggle password visibility"
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    ></AddCircleIcon>
                 
                 </InputAdornment>
              }
          />
          
        </FormControl>
        </Card.Title>
      </Card.Body>
   
        <ListGroup.Item>
        <Alert
        action={
          <Button color="inherit" size="small">
        <DeleteIcon></DeleteIcon>
          </Button>
        }
      >
        This is a success alert â€” check it out!
      </Alert>
        </ListGroup.Item>
       
      
      <Card.Body>
        <Card.Link href="#" style={{"textDecoration":"none","textAlign":"center"}}>&#169; {year} aakashkasturiya</Card.Link>
      
      </Card.Body>
    </Card>


        
        </Col>
      </Row>
    </Container>


        
        </>
    );
}

export default Main;
