import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import ButtonGroup from 'react-bootstrap/ButtonGroup'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Form from 'react-bootstrap/Form';


let Main =()=>{
 
const [count,setCount] = useState(1);


    /*Get Year */
    const year = new Date().getFullYear();
    
    var num;

    let custom = () =>{
        
        num = Number(prompt("Please enter your value!!"));
  
        setCount(count*num);

      
    }
    let customIncrement = (num) =>{
        
        setCount(count*num);
    }
    return(
        <>
            

    <Container>
      <Row>
        <Col>
        
        <h1 className='text-center'>Lets Play!!</h1>

        <Card style={{ width: '23rem'}}>
        
      <Card.Body style={{"height":"150px","backgroundColor":"#000","borderRadius":"15px"}}>
        <Card.Title style={{"fontSize":"100px","textAlign":"center",color:"#7CFC00"}}>{count}</Card.Title>
      </Card.Body>
      <ListGroup className="list-group-flush">
        <ListGroup.Item>
        <ButtonGroup size="lg" className="mb-2">
        <Button onClick={()=>setCount(1)}>Reset</Button>
        <Button onClick={()=>setCount(count+1)}>Increment</Button>
        <Button onClick={()=>setCount(count-1)}>Decrement</Button>
      </ButtonGroup>
      </ListGroup.Item>
        <ListGroup.Item>
        <ButtonGroup size="lg" className="mb-2">
        <Button onClick={()=>setCount(count*2)}>2X</Button>
        <Button onClick={custom}>Custome</Button>
        <Button onClick={(num)=>customIncrement}>++</Button>
      </ButtonGroup>
        </ListGroup.Item>
       
      </ListGroup>
      <Card.Body>
        <Card.Link href="#" style={{"textDecoration":"none","textAlign":"center"}}>&#169; {year} aakashkasturiya</Card.Link>
      
      </Card.Body>
    </Card>


        
        </Col>
      </Row>
    </Container>


        
        </>
    );
}

export default Main;
