import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Alert from '@mui/material/Alert';
import Button from '@mui/material/Button';

import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';

import AddCircleIcon from '@mui/icons-material/AddCircle';
import DeleteIcon from '@mui/icons-material/Delete';

let Main =()=>{
 



const [text, setText] = React.useState("");
const [list,setList] = React.useState([]);


const AddItem = ()=>{
setList([...list,text]);
setText("");
}

const RemoveItem = (id)=>{
  
    let updateItem = list.filter((element,index)=>{
        return id !==index;
    })
    setList(updateItem);
    }


    /*Get Year */
    const year = new Date().getFullYear();
    
    
    
    return(
        <>
            

    <Container>
      <Row>
        <Col>
        
        <h1 className='text-center'>Lets Play!!</h1>

   


        <Card style={{ width: '23rem'}}>
        
      <Card.Body style={{"height":"150px","borderRadius":"15px"}}>
        
     
        <Card.Title>
            
      <FormControl fullWidth sx={{ m: 1 }} variant="standard">
          <InputLabel htmlFor="standard-adornment-amount">Add Tasks</InputLabel>
          <Input
            id="standard-adornment-amount"
            type="text"
            value={text}
            onChange={(e)=>setText(e.target.value)}
            endAdornment={
                <InputAdornment position="end">
                    <Button  type="button" onClick={AddItem}>
                    <AddCircleIcon></AddCircleIcon>
                    </Button>
                 </InputAdornment>
              }
          />
          
        </FormControl>
        </Card.Title>
      </Card.Body>
   
        <ListGroup.Item>
            {
                list.map((item,index)=>{
                    return(
                        <>
     
     <Alert key={index}
        action={
          <Button color="inherit" size="small" type="button" onClick={()=>RemoveItem(index)}>
             <DeleteIcon></DeleteIcon>
          </Button>
        }
      >
        {item}
      </Alert>             
        
      
    
                 
                        </>
                    )
                })
            }
        
        </ListGroup.Item>
       
      
      <Card.Body style={{"textAlign":"center"}}>
        <Card.Link href="#" style={{"textDecoration":"none"}}>&#169; {year} aakashkasturiya</Card.Link>
      
      </Card.Body>
    </Card>


        
        </Col>
      </Row>
    </Container>


        
        </>
    );
}

export default Main;
